package lesson;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int v=10;
		//v=v+1;
		//v++;
		//v+=1;
		//v-=1;
		//v*=1;
		v/=5;
		
		System.out.println(v);
		
		System.out.println(v++);
		
		System.out.println(++v);
		
		System.out.println(v--);
		
		System.out.println(--v);
	}

}
